(
    sf_room_reservation_code()
   ,#{memberId}
   ,#{ldgCode}
   ,#{checkInDate}
   ,#{chekOutDate}
   ,#{reservationName}
   ,#{reservationPhone}
   ,#{userName}
   ,#{usrePhone}
   ,#{roomName}
   ,#{lodgmentDay}
   ,#{totalPrice}
   ,CURDATE()
);


	SELECT sf_new_room_code() as roomCode

